#navParent{
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    grid-template-rows: 0.5fr 1.5fr 0.5fr 1.5fr 1fr;
    gap: 0;
    pointer-events: auto;
    <insert your own background-color / backdrop filter / border-radius / gradient>
  }

#navBgndOvrlay{
    grid-area: 1 / 1 / span 7 / span 3;
    border-radius: 25px;
    z-index: 2;
    <insert your own background-color / backdrop filter >
 }

.navBtn{
    display: grid;
    grid-template-columns: 1fr 3fr 1fr;
    grid-template-rows: 2fr 0fr 1fr;
    gap: 0;
    align-items: center;
    position: relative;
    cursor: pointer;
 }

#itemID{
    grid-area: 2 / 1 / span 1 / span 1;
    z-index: 2;
    margin: 0;
    padding: 0;
 }

#navBtnImgID{
    grid-area: 1 / 2 / span 1 / span 1;
    max-width: 5rem;
    max-height: 5rem;
    position: absolute;
    left: 50%;
    top: 50%;
    transform: translate(-50%, -50%);
    z-index: 4;
    cursor: pointer;
 }

#navBtnImgID:hover{
    box-shadow: 0 0 5px 5px rgba(155, 198, 236, 0.588);
    border-radius: 50%;
    opacity: 1.0;
 }

.navLabel {
    font-size: 1.2rem;
    text-align: center;
    color: aliceblue;
    margin: 0;
    padding: 0;
 }

#navLabelID{
    grid-area: 3 / 2 / span 1 / span 1;
    z-index: 4;
    cursor: pointer;
 }

.highlight{
    box-shadow: 0 0 5px 5px rgba(155, 198, 236, 0.588);
    border-radius: 50%;
    opacity: 1.0;
 }

#socMedDiv{
    grid-area: 5 / 2 / span 1 / span 1;
    display: grid;
    grid-template-columns: repeat(4 2rem);
    grid-template-rows: repeat(2 2rem);
    gap: 0;
    align-items: center;
    position: relative;
    cursor: pointer;
 }

#socMedImgID{
    grid-area: 2 / 1 / span 1 / span 1;
    max-width: 2rem;
    max-height: 2rem;
    z-index: 4;
    cursor: pointer;
 }

#socMedImgID:hover{
    box-shadow: 0 0 5px 5px rgba(155, 198, 236, 0.588);
    opacity: 1.0;
 }
