const [showMenuOne, setShowMenuOne] = useState(false);
  const [showMenuTwo, setShowMenuTwo] = useState(false);

function handleOpening(){
      setShowMenuOne(false);
      setShowMenuTwo(false);
      var value = this.value;

      switch(value){
         case "1":
           setShowMenuOne(true);
           break;
         case "2":
           setShowMenuTwo(true);
           break;
      }
}

function handleClick(event){
   <insert function for click event>
}


function useSideBar(){
  return(
  <div>
   <Collapsible className="closeClass" openedClassName="openClass" 
    onOpening={handleOpening} open={showMenuOne} trigger="Menu One" value="1">
        <ul className="sideList" id="listOne">
            <li className="listItem" onClick={handleClick} id="itemOne">ItemOne</li>
        </ul>
   </Collapsible>
   <Collapsible className="closeClass" openedClassName="openClass" 
    onOpening={handleOpening} open={showMenuTwo} trigger="Menu Two" value="2">
         <ul className="sideList" id="listTwo">
            <li className="listItem" onClick={handleClick} id="itemTwo">ItemTwo</li>
         </ul>
   </Collapsible>
 </div>
 )
}
