import Collapsible from 'react-collapsible';
  import Button from 'react-bootstrap/Button';
  import Offcanvas from 'react-bootstrap/Offcanvas';

const [windowDimensions, setWindowDimensions] = useState(getWindowDimensions());
const [show, setShow] = useState(false);

const handleClose = () => setShow(false);
const handleShow = () => setShow(true);

useEffect(() => {
    function handleResize() {
    setWindowDimensions(getWindowDimensions()); }
    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
}, []);

function getWindowDimensions() {
    const { innerWidth: width, innerHeight: height } = window;

    return {
        width,
        height
    };
}

function useSideBar(){
    if(windowDimensions.width > 650){
       return(
           <Insert code for big viewport width>
	)
     }
     else{
       return(
          <Button id="offCanvasBtn" variant="primary" onClick={handleShow}>
            Menu
          </Button>

         <Offcanvas id="offCanvasMenu" show={show} onHide={handleClose}>
          <Offcanvas.Body>
              <Insert code for collapsible menu here>
          </Offcanvas.Body>
         </Offcanvas>
      )
   }
}
